import { useState, useContext } from 'react'; 
import CurrentUserContext from '../contexts/CurrentUserContext'; 

export default function EditProfile() {
//   const currentUser = useContext(CurrentUserContext); // Obtém o objeto de usuário atual
  const userContext = useContext(CurrentUserContext); // Obtém o objeto de usuário atual
  const { currentUser, handleUpdateUser } = userContext;

  const [name, setName] = useState(currentUser.name); // Adicione variável de estado para nome
  const [description, setDescription] = useState(currentUser.about); // Adicione variável de estado para descrição

  const handleNameChange = (event) => {
    setName(event.target.value); // Atualiza o nome (name) quando a entrada for alterada
  };

  const handleDescriptionChange = (event) => {
    setDescription(event.target.value); // Atualiza a descrição (description) quando a entrada for alterada
  };

  const handleSubmit = (event) => {
    event.preventDefault(); // Impede o comportamento padrão de envio do formulário

    handleUpdateUser({ name, about: description }); // Atualiza as informações do usuário
  };

  return (
    <form 
    className="profile-popup__form form" 
    name="formPopup"
    id="profile-popup__form" 
    onSubmit={handleSubmit}
    noValidate>
        <fieldset
        className="form__fieldset">
        <input 
        type="text" 
        className="form__input form__input_type_name" 
        id="nome" 
        name="name"
        minLength="2" 
        maxLength="40" 
        value={name} // Bind name to input
        onChange={handleNameChange} // Add onChange handler
        required />
        <span className="form__input-error nome-error"></span>
        <input 
        type="text" 
        className="form__input form__input_type_activity" 
        id="atividade" 
        name="about" 
        minLength="2" 
        maxLength="200"
        value={description} // Bind description to input
        onChange={handleDescriptionChange} // Add onChange handler 
        required />
        <span className="form__input-error atividade-error"></span>
        <button type="submit" className="form__submit-button">Salvar</button>
        </fieldset>
    </form>
  );
}
